import {
  CHANGE_PRODUCTNAME,
  CHANGE_COMPANYNAME,
  CHANGE_NAME,
  CHANGE_MAILADDRESS,
  CHANGE_COMPANYADDRESS,
  CHANGE_COMMNT,
  INITIALIZE_FORM
} from '../Action/index';

const initialState = {
  shareForm: {
    productName: '',
    companyName: '',
  },
}

const shareFormReducer = (state = initialState.shareForm, action) => {
  switch (action.type) {
    case CHANGE_PRODUCTNAME:
      return {
        ...state,
        productName: action.productName,  // actionのnameプロパティに入力された名前を入れることにする
      }
    case CHANGE_COMPANYNAME:
      return {
        ...state,
        companyName: action.companyName,  // nameと同様
      }
    case CHANGE_COMPANYNAME:
      return {
        ...state,
        companyName: action.companyName,  // nameと同様
      }
    case INITIALIZE_FORM:
      return initialState.shareForm  // 初期状態を返す
    default:
      return state
  }
}

export default shareFormReducer;
