import React from 'react';
import axios from 'axios';

const SharePage = ({
  store,
  changeProductName,
  changeCompanyName,
  changeName,
  changeMailAddress,
  changeCompanyAddress,
  changeComment,
  initializeForm,
  requestData,
  receiveDataSuccess,
  receiveDataFailed,
}) => {
  const { name, age } = store.form;

  const handleSubmit = e => {
    e.preventDefault()    // フォームsubmit時のデフォルトの動作を抑制

    axios.post('/api/characters', {
      name,
      age,
    })  // キャラクターの名前、年齢からなるオブジェクトをサーバーにPOST
      .then(response => {
        initializeForm() // submit後はフォームを初期化
        const characterArray = response.data;
        receiveDataSuccess(characterArray);
      })
      .catch(err => {
        receiveDataFailed();
        console.error(new Error(err))
      })
  }

  return (
    <div>
      <form onSubmit={e => handleSubmit(e)}>


        <label>
          シェアリングしたい商品:
          <input value={age} onChange={e => changeProductName(e.target.value)} />
        </label>
        <label>
          会社名:
          <input value={age} onChange={e => changeCompanyName(e.target.value)} />
        </label>
        <label>
          名前:
          <input value={age} onChange={e => changeName(e.target.value)} />
        </label>
        <label>
          メールアドレス:
          <input value={age} onChange={e => changeMailAddress(e.target.value)} />
        </label>
        <label>
          住所:
          <input value={age} onChange={e => changeCompanyAddress(e.target.value)} />
        </label>
        <label>
          コメント:
          <input value={age} onChange={e => changeComment(e.target.value)} />
        </label>

        <button type="submit">submit</button>
      </form>
    </div>
  )
}

export default SharePage;
