import React from 'react';
import axios from 'axios';
import Button from '@material-ui/core/Button';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';
import './style.css';

class MyForm extends React.Component {

  handleSubmit = e => {
    e.preventDefault()    // フォームsubmit時のデフォルトの動作を抑制

    const {
      store,
      initializeForm,
      receiveDataFailed,
    } = this.props;

    const {
      productName,
      companyName,
      name,
      mailAddress,
      companyAddress,
      comment
    } = store.shareForm;

    axios.post('/api/share', {
      productName,
      companyName,
      name,
      mailAddress,
      companyAddress,
      comment
    })                    // オブジェクトをサーバーにPOST
      .then(response => {
        initializeForm(); // submit後はフォームを初期化
        console.log(response);
        /*const characterArray = response.data;
        receiveDataSuccess(characterArray);*/
      })
      .catch(err => {
        receiveDataFailed();
        console.error(new Error(err))
      })
  }

  render() {
    const {
      store,
      changeProductName,
      changeCompanyName,
      changeName,
      changeMailAddress,
      changeCompanyAddress,
      changeComment,
    } = this.props;

    const {
      productName,
      companyName,
      name,
      mailAddress,
      companyAddress,
      comment
    } = store.shareForm;



    return (
      <div className="form">
        <ValidatorForm
          ref="form"
          onSubmit={this.handleSubmit}
          onError={errors => console.log(errors)}
        >
          <TextValidator
            label="シェアリングしたい商品"
            onChange={e => changeProductName(e.target.value)}
            name="email"
            value={productName}
            validators={['required', 'isEmail']}
            errorMessages={['this field is required', 'email is not valid']}
          />
          <TextValidator
            label="会社名"
            onChange={e => changeCompanyName(e.target.value)}
            value={companyName}
            name="email"
            validators={['required', 'isEmail']}
            errorMessages={['this field is required', 'email is not valid']}
          />
          <TextValidator
            label="名前"
            onChange={e => changeName(e.target.value)}
            value={name}
            name="email"
            validators={['required', 'isEmail']}
            errorMessages={['this field is required', 'email is not valid']}
          />
          <TextValidator
            label="メールアドレス"
            onChange={e => changeMailAddress(e.target.value)}
            value={mailAddress}
            name="email"
            validators={['required', 'isEmail']}
            errorMessages={['this field is required', 'email is not valid']}
          />
          <TextValidator
            label="住所"
            onChange={e => changeCompanyAddress(e.target.value)}
            value={companyAddress}
            name="email"
            validators={['required', 'isEmail']}
            errorMessages={['this field is required', 'email is not valid']}
          />
          <TextValidator
            label="コメント"
            onChange={e => changeComment(e.target.value)}
            value={comment}
            name="email"
            validators={['required', 'isEmail']}
            errorMessages={['this field is required', 'email is not valid']}
          />
          <Button type="submit">Submit</Button>
        </ValidatorForm>
      </div>
    );
  }
}

export default MyForm;
